import React from 'react';
import { motion } from 'framer-motion';
import { PencilSquareIcon, LightBulbIcon, ChatBubbleLeftRightIcon, BookOpenIcon } from '@heroicons/react/24/outline';

const cards = [
  {
    title: 'Tech Insights',
    desc: 'Read the latest articles on technology and innovation.',
    icon: LightBulbIcon,
    color: 'from-yellow-100/80 to-yellow-50/80',
  },
  {
    title: 'Career Advice',
    desc: 'Get tips and advice for your career journey.',
    icon: PencilSquareIcon,
    color: 'from-yellow-50/80 to-yellow-100/80',
  },
  {
    title: 'Community Stories',
    desc: 'Explore inspiring stories from the DEVNoVATE community.',
    icon: ChatBubbleLeftRightIcon,
    color: 'from-yellow-100/80 to-yellow-200/80',
  },
  {
    title: 'Learning Resources',
    desc: 'Discover curated resources to help you grow.',
    icon: BookOpenIcon,
    color: 'from-yellow-200/80 to-yellow-100/80',
  },
];

const Blogs = () => (
  <div className="max-w-5xl mx-auto py-12 px-4">
    <h1 className="text-3xl font-bold text-yellow-900 mb-6">Blogs</h1>
    <motion.div
      className="grid grid-cols-1 md:grid-cols-2 gap-6"
      initial="hidden"
      animate="visible"
    >
      {cards.map((card, i) => (
        <motion.div
          key={card.title}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: i * 0.15 }}
          whileHover={{ scale: 1.04 }}
          className={`glassmorphism bg-gradient-to-br ${card.color} rounded-xl shadow-xl p-6 flex flex-col items-center`}
        >
          <card.icon className="h-10 w-10 text-yellow-600 mb-3 animate-bounce" />
          <h2 className="font-semibold text-yellow-900 mb-2">{card.title}</h2>
          <p className="text-yellow-700 text-sm text-center">{card.desc}</p>
        </motion.div>
      ))}
    </motion.div>
  </div>
);

export default Blogs;
