import React from 'react';
import { motion } from 'framer-motion';
import { BookOpenIcon, UsersIcon, VideoCameraIcon, AcademicCapIcon } from '@heroicons/react/24/outline';

const cards = [
  {
    title: 'Bundled Courses',
    desc: 'Curated courses to boost your skills in tech, business, and more.',
    icon: BookOpenIcon,
    color: 'from-blue-200/80 to-blue-100/80',
  },
  {
    title: 'Live Cohorts',
    desc: 'Join live learning sessions and interact with mentors and peers.',
    icon: UsersIcon,
    color: 'from-blue-100/80 to-blue-200/80',
  },
  {
    title: 'Self Paced',
    desc: 'Learn at your own pace with flexible online modules.',
    icon: AcademicCapIcon,
    color: 'from-blue-50/80 to-blue-100/80',
  },
  {
    title: 'Video Tutorials',
    desc: 'Watch high-quality video lessons from industry experts.',
    icon: VideoCameraIcon,
    color: 'from-blue-100/80 to-blue-300/80',
  },
];

const Learn = () => (
  <div className="max-w-5xl mx-auto py-12 px-4">
    <h1 className="text-3xl font-bold text-blue-900 mb-6">Learn</h1>
    <motion.div
      className="grid grid-cols-1 md:grid-cols-2 gap-6"
      initial="hidden"
      animate="visible"
    >
      {cards.map((card, i) => (
        <motion.div
          key={card.title}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: i * 0.15 }}
          whileHover={{ scale: 1.04 }}
          className={`glassmorphism bg-gradient-to-br ${card.color} rounded-xl shadow-xl p-6 flex flex-col items-center`}
        >
          <card.icon className="h-10 w-10 text-blue-600 mb-3 animate-bounce" />
          <h2 className="font-semibold text-blue-900 mb-2">{card.title}</h2>
          <p className="text-blue-700 text-sm text-center">{card.desc}</p>
        </motion.div>
      ))}
    </motion.div>
  </div>
);

export default Learn;
