import React from 'react';

const Host = () => (
  <div className="max-w-3xl mx-auto py-12 px-4">
    <h1 className="text-3xl font-bold text-primary-700 mb-6">Host an Opportunity</h1>
    <div className="bg-primary-50 rounded-xl p-8 shadow-lg flex flex-col items-center">
      <span className="w-20 h-20 bg-white rounded-full mb-4 border-2 border-primary-200"></span>
      <h2 className="font-semibold text-primary-700 mb-2">Share your event with the DEVNoVATE community!</h2>
      <p className="text-primary-800 text-center mb-4">Fill out the form to host a hackathon, challenge, or learning event. Reach thousands of talented students and professionals.</p>
      <button className="px-6 py-3 bg-primary-600 text-white rounded-lg font-semibold hover:bg-primary-700 transition-colors duration-150">Get Started</button>
    </div>
  </div>
);

export default Host;
