import React from 'react';
import { motion } from 'framer-motion';
import { BriefcaseIcon, AcademicCapIcon, CurrencyDollarIcon, ClockIcon } from '@heroicons/react/24/outline';

const cards = [
  {
    title: 'Full-Time Jobs',
    desc: 'Apply for full-time roles at top companies.',
    icon: BriefcaseIcon,
    color: 'from-blue-200/80 to-blue-100/80',
  },
  {
    title: 'Part-Time Internships',
    desc: 'Find part-time internships to gain experience.',
    icon: ClockIcon,
    color: 'from-blue-100/80 to-blue-200/80',
  },
  {
    title: 'Paid Internships',
    desc: 'Explore paid internship opportunities.',
    icon: CurrencyDollarIcon,
    color: 'from-blue-50/80 to-blue-100/80',
  },
  {
    title: 'Campus Ambassador',
    desc: 'Represent companies on your campus and gain leadership experience.',
    icon: AcademicCapIcon,
    color: 'from-blue-100/80 to-blue-300/80',
  },
];

const Jobs = () => (
  <div className="max-w-5xl mx-auto py-12 px-4">
    <h1 className="text-3xl font-bold text-blue-900 mb-6">Jobs</h1>
    <motion.div
      className="grid grid-cols-1 md:grid-cols-2 gap-6"
      initial="hidden"
      animate="visible"
    >
      {cards.map((card, i) => (
        <motion.div
          key={card.title}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: i * 0.15 }}
          whileHover={{ scale: 1.04 }}
          className={`glassmorphism bg-gradient-to-br ${card.color} rounded-xl shadow-xl p-6 flex flex-col items-center`}
        >
          <card.icon className="h-10 w-10 text-blue-600 mb-3 animate-bounce" />
          <h2 className="font-semibold text-blue-900 mb-2">{card.title}</h2>
          <p className="text-blue-700 text-sm text-center">{card.desc}</p>
        </motion.div>
      ))}
    </motion.div>
  </div>
);

export default Jobs;
