import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { UserGroupIcon, TrophyIcon } from '@heroicons/react/24/outline';

const OrganizerDashboard = () => {
  const [hackathons] = useState([
    {
      id: 1,
      title: 'AI Innovation Challenge 2024',
      date: '2024-04-15',
      participants: 25,
      judges: 5,
      status: 'Active',
    },
    {
      id: 2,
      title: 'Web3 Development Hackathon',
      date: '2024-05-01',
      participants: 15,
      judges: 3,
      status: 'Upcoming',
    },
  ]);

  return (
    <div className="space-y-8">
      <h1 className="text-2xl font-bold text-yellow-900">Organizer Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          whileHover={{ scale: 1.03 }}
          className="bg-yellow-100 rounded-xl shadow-lg p-6 flex flex-col items-center"
        >
          <TrophyIcon className="h-10 w-10 text-yellow-600 mb-2" />
          <h2 className="font-semibold text-yellow-900 mb-2">Manage Hackathons</h2>
          <p className="text-yellow-800 text-sm text-center mb-2">Create, edit, and monitor all ongoing and upcoming hackathons.</p>
          <ul className="w-full mt-2 space-y-2">
            {hackathons.map(h => (
              <li key={h.id} className="flex justify-between items-center bg-white rounded-lg px-3 py-2 shadow">
                <span className="font-medium text-yellow-900">{h.title}</span>
                <span className="text-xs text-yellow-700">{h.status}</span>
              </li>
            ))}
          </ul>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          whileHover={{ scale: 1.03 }}
          className="bg-yellow-100 rounded-xl shadow-lg p-6 flex flex-col items-center"
        >
          <UserGroupIcon className="h-10 w-10 text-yellow-600 mb-2" />
          <h2 className="font-semibold text-yellow-900 mb-2">Participants</h2>
          <p className="text-yellow-800 text-sm text-center mb-2">View and manage all registered participants for your events.</p>
          <span className="text-2xl font-bold text-yellow-700">40+</span>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          whileHover={{ scale: 1.03 }}
          className="bg-yellow-100 rounded-xl shadow-lg p-6 flex flex-col items-center"
        >
          <UserGroupIcon className="h-10 w-10 text-yellow-600 mb-2" />
          <h2 className="font-semibold text-yellow-900 mb-2">Judges</h2>
          <p className="text-yellow-800 text-sm text-center mb-2">Assign, invite, and manage judges for your hackathons.</p>
          <span className="text-2xl font-bold text-yellow-700">8</span>
        </motion.div>
      </div>
    </div>
  );
};

export default OrganizerDashboard;
