import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { HeartIcon, HeartIcon as HeartIconSolid, UserGroupIcon, BriefcaseIcon } from '@heroicons/react/24/outline';

const cardVariants = {
  hidden: { opacity: 0, y: 30 },
  visible: (i) => ({ opacity: 1, y: 0, transition: { delay: i * 0.15 } }),
};

const RecruiterDashboard = () => {
  const [candidates, setCandidates] = useState([
    {
      id: 1,
      name: 'John Doe',
      role: 'Full Stack Developer',
      projects: [
        {
          title: 'AI-Powered Task Manager',
          description: 'A smart task management system with AI suggestions',
          githubLink: 'https://github.com/example/task-manager',
          hackathon: 'AI Innovation Challenge 2024',
          rating: 9,
        },
      ],
      skills: ['React', 'Node.js', 'Python', 'Machine Learning'],
      interested: true,
    },
    {
      id: 2,
      name: 'Jane Smith',
      role: 'Blockchain Developer',
      projects: [
        {
          title: 'Blockchain Voting System',
          description: 'Secure and transparent voting system using blockchain',
          githubLink: 'https://github.com/example/voting-system',
          hackathon: 'Web3 Development Hackathon',
          rating: 8,
        },
      ],
      skills: ['Solidity', 'Web3.js', 'React', 'Node.js'],
      interested: false,
    },
  ]);

  const toggleInterest = (candidateId) => {
    setCandidates(
      candidates.map((c) =>
        c.id === candidateId ? { ...c, interested: !c.interested } : c
      )
    );
  };

  return (
    <div className="space-y-8">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-green-900 mb-1">Welcome, Recruiter! 💼</h1>
          <p className="text-green-700">Browse top talent, mark interest, and connect with candidates!</p>
        </div>
        <motion.button
          whileHover={{ scale: 1.08 }}
          whileTap={{ scale: 0.96 }}
          className="flex items-center px-4 py-2 bg-gradient-to-r from-green-400 to-green-600 text-white rounded-lg shadow-lg font-semibold"
        >
          <BriefcaseIcon className="h-5 w-5 mr-2 animate-bounce" />
          Post Job
        </motion.button>
      </div>
      <motion.div className="grid grid-cols-1 gap-6" initial="hidden" animate="visible">
        <motion.div
          variants={cardVariants}
          custom={0}
          className="glassmorphism bg-gradient-to-br from-green-100/80 to-green-200/80 rounded-xl shadow-xl p-6"
        >
          <div className="flex items-center mb-4">
            <UserGroupIcon className="h-7 w-7 text-green-600 mr-2 animate-pulse" />
            <h2 className="text-xl font-semibold text-green-900">Candidate Profiles</h2>
          </div>
          <div className="space-y-6">
            <AnimatePresence>
              {candidates.map((candidate, i) => (
                <motion.div
                  key={candidate.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 20 }}
                  transition={{ delay: i * 0.1 }}
                  whileHover={{ scale: 1.03 }}
                  className="p-6 bg-white/70 dark:bg-gray-700/70 rounded-lg shadow"
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-medium text-green-900">{candidate.name}</h3>
                      <p className="text-sm text-green-700">{candidate.role}</p>
                    </div>
                    <motion.button
                      whileHover={{ scale: 1.2 }}
                      whileTap={{ scale: 0.9 }}
                      onClick={() => toggleInterest(candidate.id)}
                      className={`p-2 rounded-full ${
                        candidate.interested
                          ? 'text-red-500'
                          : 'text-gray-400 hover:text-red-500'
                      }`}
                    >
                      {candidate.interested ? (
                        <HeartIconSolid className="h-6 w-6" />
                      ) : (
                        <HeartIcon className="h-6 w-6" />
                      )}
                    </motion.button>
                  </div>
                  <div className="mt-4">
                    <h4 className="text-sm font-medium text-green-700 mb-2">Skills</h4>
                    <div className="flex flex-wrap gap-2">
                      {candidate.skills.map((skill) => (
                        <span
                          key={skill}
                          className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div className="mt-4">
                    <h4 className="text-sm font-medium text-green-700 mb-2">Projects</h4>
                    <div className="space-y-3">
                      {candidate.projects.map((project, index) => (
                        <div
                          key={index}
                          className="p-3 bg-white/80 rounded-lg border border-green-200"
                        >
                          <div className="flex justify-between items-start">
                            <div>
                              <h5 className="font-medium text-green-900">{project.title}</h5>
                              <p className="text-sm text-green-700 mt-1">
                                {project.description}
                              </p>
                              <p className="text-sm text-green-500 mt-1">
                                Hackathon: {project.hackathon}
                              </p>
                            </div>
                            <div className="flex items-center space-x-2">
                              <span className="text-sm font-medium text-green-700">
                                Rating: {project.rating}/10
                              </span>
                              <a
                                href={project.githubLink}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="text-sm text-green-600 hover:text-green-800 underline"
                              >
                                View on GitHub
                              </a>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </motion.div>
              ))}
            </AnimatePresence>
          </div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default RecruiterDashboard;
